import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
	const [input, setInput] = useState('');
	const [hash, setHash] = useState('');
	const [error, setError] = useState('');

	const handleHash = async () => {
		try {
			const response = await axios.post('http://localhost:8080/api/hash/sha256', input, {
				headers: { 'Content-Type': 'text/plain' },
			});
			console.log(response.data);
			setHash(response.data);
			setError('');
		} catch (err) {
			setError('Error hashing input: ' + err.message);
			setHash('');
		}
	};

	return (
		/* Main Page */
		<div className="main d-flex">
			{/* Sidebar */}
			<div className="navbar bg-white me-1 px-2 rounded w-25">
				<div className="d-flex flex-column w-100">
					<button className="btn btn-outline-warning m-2 text-danger">SHA-256</button>
					<button className="btn btn-outline-warning m-2 text-danger">SHA-512</button>
					<button className="btn btn-outline-warning m-2 text-danger">Other</button>

				</div>
			</div>
			
			{/* Content Area */}
			<div className="card ms-1 p-3 bg-black w-75">
					<div className="card m-3 p-3" style={{ borderBottom: 'none', borderRadius: '10px' }}>
						<h1>SHA-256 Hashing</h1>
						<div className="m-2 mr-2 p-2">
							<textarea
								value={input}
								onChange={(e) => setInput(e.target.value)}
								placeholder="Enter text to hash"
								rows="4"
								className="form-control"
							/>
						</div>
						<div className="">
							<button className="btn btn-success w-50" onClick={handleHash}>Hash</button>
						</div>
					</div>
					<div className="card m-3 p-3" style={{ borderTop: 'none', borderRadius: '10px' }}>
						<div className="mt-2">
							{hash && (
								<div className="w-100">
									<h3>Hashed Output (SHA-256)</h3>
									<p className="hash-text">{hash}</p>
								</div>
							)}
							{error && <p style={{ color: 'red' }}>{error}</p>}
						</div>
					</div>
			</div>
		</div>

	);
}

export default App;
