import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
	const [input, setInput] = useState('');
	const [hash, setHash] = useState('');
	const [error, setError] = useState('');

	const handleHash = async () => {
		try {
			const response = await axios.post('http://localhost:8080/api/hash/sha256', input, {
				headers: { 'Content-Type': 'text/plain' },
			});
			console.log(response.data);
			setHash(response.data);
			setError('');
		} catch (err) {
			setError('Error hashing input: ' + err.message);
			setHash('');
		}
	};

	return (
		<div className="container m-3">
			<div className="card p-3 justify-content-center align-items-center">
				<h1>SHA-256 Hashing</h1>
				<div>
					<textarea
						value={input}
						onChange={(e) => setInput(e.target.value)}
						placeholder="Enter text to hash"
						rows="4"
						cols="55"
					/>
				</div>
				<button className="btn btn-success" onClick={handleHash}>Hash</button>
				<div className="card m-4 p-4" style={{width: '450px'}}>
					{hash && (
						<div className="">
							<h3>Hashed Output (SHA-256):</h3>
							<p>{hash}</p>
						</div>
					)}
					{error && <p style={{ color: 'red' }}>{error}</p>}
				</div>
			</div>
		</div>
	);
}

export default App;
